<?php/*Template Name: ROI Template*/get_header();$is_page_builder_used = et_pb_is_pagebuilder_used( get_the_ID() ); ?>	<script>	  (function($){		var previouslySubmitted = false;		var calcFields = $('.calc');		var priceFields = $('.price');		var roundFields = $('.round');		var captureFields = $('.capture');		var pctFields = $('.pct');		priceFields.html('$0');		$('.number').keyup(function () {			this.value = this.value.replace(/[^0-9\.]/g,'');		});		var reqFields = $('input.required');		var	fleetSize = $('input[name=fleetsize]'),			fuelSaveMonth = $('input[name=fuelsavemonth]'),			reduceMiles = $('select[name=reducemiles]'),			reduceIdle = $('select[name=reduceidle]'),			reducedLabor = $('select[name=reducedlabor]'),			milesSavedMonth = $('input[name=milessavedmonth]'),			workdays = $('input[name=workdays]'),			wearTear = $('input[name=weartear]'),			galsSaved = $('input[name=galssaved]'),			mpg = $('input[name=mpg]'),			employeeWage = $('input[name=employeewage]'),			fuelSavedDay = $('input[name=fuelsavedday]'),			fuelCost = $('input[name=fuelcost]'),			saveMonthMiles = $('input[name=savemonthmiles]'),			idleHours = $('input[name=idlehours]'),			galsSavedMonth = $('input[name=galssavedmonth]'),			saveReduceIdle = $('input[name=savereduceidle]'),			minsMonth = $('input[name=minsmonth]'),			perEmployee = $('input[name=peremployee]'),			reducedLaborMonth = $('input[name=reducedlabormonth]'),			totalMonthSavings = $('input[name=totalmonthsavings]'),			avgMonthCost = $('input[name=avgmonthcost]'),			roiInMonth = $('input[name=roiinmonth]'),			columns = $('.column:nth-child(2) .row label, .column:nth-child(2) .row .calc, .column:nth-child(2) span, .column:nth-child(3) .row label, .column:nth-child(3) .row .calc, .column:nth-child(3) span');			columns.addClass('disabled');			$('#totalsavingsblock').addClass('disabled2');			reqFields.parent().removeClass('error');			$('.resetbutton').click(function(){				captureFields.not('input[name=name],input[name=company],input[name=email]').val('');				calcFields.html('0');				priceFields.html('$0');				reqFields.parent().removeClass('error');			});		var crunchNumbers = function(){			var round = function(num){				return Math.round( num * 100) / 100;			};			calcFields.empty();			milesSavedMonth.val(reduceMiles.val() * fleetSize.val() * workdays.val());			wearTear.val(milesSavedMonth.val() * 0.85);			galsSaved.val( round(reduceMiles.val() / mpg.val()) );			fuelSavedDay.val(fleetSize.val() * galsSaved.val() * fuelCost.val());			saveMonthMiles.val( round(fuelSavedDay.val() * workdays.val()) );			idleHours.val((reduceIdle.val() * fleetSize.val() * workdays.val())/60);			galsSavedMonth.val(0.5 * idleHours.val());			saveReduceIdle.val(galsSavedMonth.val() * fuelCost.val());			fuelSaveMonth.val( round(saveMonthMiles.val()) + round(saveReduceIdle.val()) );			minsMonth.val(reducedLabor.val() * workdays.val());			perEmployee.val((minsMonth.val()/60) * employeeWage.val());			reducedLaborMonth.val(perEmployee.val() * fleetSize.val());			totalMonthSavings.val( round(wearTear.val()) + round(saveMonthMiles.val()) + round(saveReduceIdle.val()) + round(reducedLaborMonth.val()) );			avgMonthCost.val( 40 * fleetSize.val() );			roiInMonth.val( (totalMonthSavings.val() - avgMonthCost.val())/avgMonthCost.val() * 100 );			calcFields.each(function(){				var that = $(this);				var price = that.siblings('input').val();				that.html(price);			});			$('#galssaved').currency({ decimals: 2, hidePrefix: true });			priceFields.currency({ decimals: 2 });			roundFields.currency({ decimals: 2, hidePrefix: true });			pctFields.html(function(num, val){				return val + '%';			});		};		jQuery('form').submit(function(event){			var flagFields = $('input[value=""].required');			reqFields.parent().removeClass('error');			if( flagFields.length ){				flagFields.parent().addClass('error');				event.preventDefault();				return false;			} else {				flagFields.parent().removeClass('error');			}			crunchNumbers();			columns.removeClass('disabled');			$('#totalsavingsblock').removeClass('disabled2');			var formData = {};			captureFields.each(function(){				var that = $(this), value, name;				var label = that.siblings('label');				if( that.is('div') ){					name = that.attr('id');					value = that.html();				} else {					name = that.attr('name');					value = that.val();				}				formData[name] = {					label: label.text(),					value: value				}			});			formData.refurl = {				label: 'Referring Url',				value: document.referrer			};			if(previouslySubmitted == false) {				$.ajax({					url: 'roiform.php',					type: 'POST',					data: formData,					success: function(){						previouslySubmitted = true;					}				});			}			event.preventDefault();		});	  })(jQuery);	</script></head><body><form style="margin: 0;">	<table id="fields" style="display:inline-block;">		<tr><td class="column">		<span>Fleet Info</span>		<div class="row">			<label>Name</label>			<input type="text" size="40" id="name" name="name" class="required capture"/>		</div>		<div class="row">			<label>Company</label>			<input type="text" size="40" id="company" name="company" class="required capture"/>		</div>		<div class="row">			<label>Email</label>			<input type="email" size="40" id="email" name="email" class="required capture"/>		</div>		<div class="row">			<label>Fleet Size</label>			<input type="text" size="10" id="fleetsize" name="fleetsize" class="required number capture"/>		</div>		<div class="row">			<label>Average MPG per Vehicle</label>			<input type="text" size="10" id="mpg" name="mpg" class="required number capture"/>		</div>		<div class="row">			<label>Employee Wage per Hour ($)</label>			<input type="text" size="10" id="employeewage" name="employeewage" class="required number capture"/>		</div>		<div class="row">			<label>Desired Daily Mileage Reduction per Vehicle</label>			<select id="reducemiles" name="reducemiles" class="required number capture">				<option value="15">15 Miles				<option value="10">10 Miles				<option value="5">5 Miles			</select>		</div>		<div class="row">			<label>How Many Work Days per Month</label>			<input type="text" size="10" id="workdays" name="workdays" class="required number capture"/>		</div>		<div class="row">			<label>Desired Daily Reduction of Idle Time per Vehicle</label>			<select id="reduceidle" name="reduceidle" class="required number capture">				<option value="60">60 Minutes				<option value="45">45 Minutes				<option value="30">30 Minutes				<option value="15" selected>15 Minutes				<option value="10">10 Minutes				<option value="5">5 Minutes			</select>		</div>		<div class="row">			<label>Average Cost per Gallon of Fuel ($)</label>			<input type="text" size="10" id="fuelcost" name="fuelcost" class="required number capture"/>		</div>		<div class="row">			<label>Desired Daily Reduction of Paid Time per Employee</label>			<select id="reducedlabor" name="reducedlabor" class="required number capture">				<option value="30">30 Minutes				<option value="15" selected>15 Minutes				<option value="10">10 Minutes				<option value="5">5 Minutes			</select>		</div>			<button class="action">Calculate ROI</button><button class="resetbutton" style="margin-left: 10px;" onclick="return false;">Reset</button></td><td class="column">	<span>Maintenance Savings</span>	<div class="row">		<label>Miles Saved per Month</label>		<div id="milessavedmonth" class="calc round capture">0</div>		<input type="hidden" name="milessavedmonth" value="0.00" />	</div>	<div class="row">		<label>Saved in Wear &amp; Tear per Month</label>		<div id="weartear" class="calc price capture">0</div>		<input type="hidden" name="weartear" value="0.00" />	</div>	<br><span>Fuel Savings</span><div class="row">	<label>Gallons Saved per Day, per Vehicle</label>	<div id="galssaved" class="calc round capture">0</div>	<input type="hidden" name="galssaved" value="0.00" /></div><div class="row">	<label>Fuel Savings per Day</label>	<div id="fuelsavedday" class="calc price capture">0</div>	<input type="hidden" name="fuelsavedday" value="0.00" /></div><div class="row">	<label>Saved in Reduced Miles per Month</label>	<div id="savemonthmiles" class="calc price capture">0</div>	<input type="hidden" name="savemonthmiles" value="0.00" /></div><div class="row">	<label>Idling Hours Reduced per Month</label>	<div id="idlehours" class="calc round capture">0</div>	<input type="hidden" name="idlehours" value="0.00" /></div><div class="row">	<label>Gallons Saved per Month</label>	<div id="galssavedmonth" class="calc round capture">0</div>	<input type="hidden" name="galssavedmonth" value="0.00" /></div><div class="row">	<label for="savereduceidle">Saved in Reduced Idle Time per Month</label>	<div id="savereduceidle" class="calc price capture">0</div>	<input type="hidden" name="savereduceidle" value="0.00" /></div><div class="row">	<label>Total Fuel Saved per Month</label>	<div id="fuelsavemonth" class="calc price capture">0</div>	<input type="hidden" name="fuelsavemonth" value="0.00" /></div></td><td class="column">	<span>Labor Savings</span>	<div class="row">		<label>Minutes Saved per Month</label>		<div id="minsmonth" class="calc round capture">0</div>		<input type="hidden" name="minsmonth" value="0.00" />	</div>	<div class="row">		<label>Per Employee per Month</label>		<div id="peremployee" class="calc price capture">0</div>		<input type="hidden" name="peremployee" value="0.00" />	</div>	<div class="row">		<label>Total Reduced Labor per Month</label>		<div id="reducedlabormonth" class="calc price capture">0</div>		<input type="hidden" name="reducedlabormonth" value="0.00" />	</div>	<br>	<div id="totalsavingsblock" style="background-color: #EBEBEB; border: 2px solid #D1D1D1; padding: 4px;">		<span>Total Savings</span>		<div class="row">			<label>Total Monthly Savings</label>			<div id="totalmonthsavings" class="calc price capture">0</div>			<input type="hidden" name="totalmonthsavings" value="0.00" />		</div>		<div class="row">			<label>Average Monthly Cost for GPS</label>			<div id="avgmonthcost" class="calc price capture">0</div>			<input type="hidden" name="avgmonthcost" value="0.00" />		</div>		<div class="row">			<label>ROI in 1 Month</label>			<div id="roiinmonth" class="calc round pct capture">0</div>			<input type="hidden" name="roiinmonth" value="0.00" />		</div>	</div>	<br></td></tr></table><div style="position:absolute; left:-9999px; top: -9999px;"><label for="pardot_extra_field">Comments</label><input type="text" id="pardot_extra_field" name="pardot_extra_field"></div></form><?php get_footer(); ?>